    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        // TODO add your handling code here:
        int dias = Integer.parseInt(cbxDias.getSelectedItem().toString());
        VentaControlador vc = new VentaControlador();
        List<String[]> prediccion = vc.obtenerProductosMasVendidos(dias);

        DefaultTableModel modelo = new DefaultTableModel();
        modelo.setColumnIdentifiers(new String[]{"CÃ³digo", "Nombre", "Cantidad Vendida"});
        for (String[] fila : prediccion) {
            modelo.addRow(fila);
        }
        tblPrediccion.setModel(modelo);
    }//GEN-LAST:event_btnMostrarActionPerformed

